import React, { useState, useEffect } from 'react';
import { Line } from '@ant-design/charts';
import { DatePicker } from 'antd';
const DemoLine = () => {
    const [data, setData] = useState([]);
    useEffect(() => {
        asyncFetch();
    }, []);
    const asyncFetch = () => {
        var sock = new WebSocket("ws://192.168.1.11:3000"); //replace this address with the one the node.js server prints out. keep port 3000

        sock.onopen = function (event) {
            //alert("Socket connected succesfully!!!")
            console.log("Connection Successful");

        };
        sock.onmessage = function (event) {
            console.log(event); //show received from server data in console of browser
            var values = event.data.split(":");
            var datadetails = {
                "voltage": values[0],
                "current": values[1],
                "power": values[2],
                "energy": values[3]
            }

            var time = new Date();
            var y = time.getHours() + ":" + time.getMinutes() + ":" + time.getSeconds();
            var x = [{
                "date": y,
                "value": values[0],
                "category": "Voltage"
            },
            {
                "date": y,
                "value": values[1],
                "category": "Current"
            },
            {
                "date": y,
                "value": values[2],
                "category": "Power"
            },
            {
                "date": y,
                "value": values[3],
                "category": "Energy"
            }]
            setData([...data, datadetails]);
        }
    };



    var config = {
        data: data,
        padding: 'auto',
        xField: 'date',
        yField: 'power',
        xAxis: { tickCount: 5 },
    };
    return <Line {...config} />;
};
export default DemoLine;
