import React, { useState, useEffect } from 'react';
import { Line } from '@ant-design/charts';
const DemoLine = () => {
    const [data, setData] = useState([]);
    useEffect(() => {
        asyncFetch();
    }, []);
    const asyncFetch = () => {
        var sock = new WebSocket("ws://192.168.1.11:3000"); //replace this address with the one the node.js server prints out. keep port 3000

        sock.onopen = function (event) {
            //alert("Socket connected succesfully!!!")
            console.log("Connection Successful");

        };
        sock.onmessage = function (event) {
            console.log(event); //show received from server data in console of browser
            display.innerHTML += event.data + "<br />"; //add incoming message from server to the log screen previous string + new string(message)

        }
    };



    var config = {
        data: data,
        padding: 'auto',
        xField: 'date',
        yField: 'power',
        xAxis: { tickCount: 5 },
    };
    return <Line {...config} />;
};
export default DemoLine;
